// define a function that takes an array of integers and returns the maximum sum of any contiguous subarray
function f_gold(gain) {
    var t = 0; // initialize a variable to keep track of the current sum
    var res = t; // initialize a variable to keep track of the maximum sum seen so far
    for (var h of gain) { // loop through the array
        t += h; // add the current element to the current sum
        res = Math.max(res, t); // update the maximum sum seen so far if necessary
    }
    return res; // return the maximum sum seen
}
