from typing import *
def f_gold(n: int, edges: List[List[int]]) -> int:
    def dfs(i):
        vis[i] = True
        res = 1
        for j in g[i]:
            if not vis[j]:
                res += dfs(j)
        return res
    g = defaultdict(list)
    for a, b in edges:
        g[a].append(b)
        g[b].append(a)
    vis = [False] * n
    arr = []
