const { Counter } = require('collections');

function longestPalindrome(s) {
    const n = s.length;
    const counter = new Counter(s);
    const odd_cnt = [...counter.values()].reduce((acc, e) => acc + (e % 2), 0);
    return odd_cnt === 0 ? n : n - odd_cnt + 1;
}
