// define a function to check if a number is a valid gold number
function f_gold(num) {
    // define a helper function to check if two digits are a valid pair
    function match(a, b) {
        if (['0', '1', '8'].includes(a)) {
            return a == b;
        }
        if (a == '6') {
            return b == '9';
        }
        if (a == '9') {
            return b == '6';
        }
        return false;
    }
    // initialize variables
    var n = num.length;
    var i = 0;
    var j = n - 1;
    // loop through the number and check if each pair is valid
    while (i <= j) {
        if (!match(num[i], num[j])) {
            return false;
        }
        i += 1;
        j -= 1;
    }
    // if all pairs are valid, return true
    return true;
}
