function canFinish(numOfCourses, prerequisites) {
    let edges = new Map();
    let indegree = new Array(numOfCourses).fill(0);
    for (let i = 0; i < prerequisites.length; i++) {
        let course = prerequisites[i][0];
        let prerequisite = prerequisites[i][1];
        if (edges.has(prerequisite)) {
            edges.get(prerequisite).push(course);
        } else {
            edges.set(prerequisite, [course]);
        }
        indegree[course] += 1;
    }
    let queue = [];
    for (let i = 0; i < numOfCourses; i++) {
        if (indegree[i] == 0) {
            queue.push(i);
        }
    }
    let count = 0;
    while (queue.length > 0) {
        let prerequisite = queue.shift();
        count += 1;
        for (let i = 0; i < edges.get(prerequisite)?.length; i++) {
            let course = edges.get(prerequisite)[i];
            indegree[course] -= 1;
            if (indegree[course] == 0) {
                queue.push(course);
            }
        }
    }
    return count == numOfCourses;
}
