function calculate_diagonal_sum(matrix) {
    var matrix_size = matrix.length;
    var diagonal_sum = 0;
    for (var i = 0; i < matrix_size; i++) {
        diagonal_sum += matrix[i][i] + ((matrix_size - i - 1 == i) ? 0 : matrix[i][matrix_size - i - 1]);
    }
    return diagonal_sum;
}
