            p[x] = find(p[x]);
        }
        return p[x];
    }
    const n = grid.length;
    const p = [...Array(n * n).keys()];
    const hi = new Array(n * n).fill(0);
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
