// function to calculate the product of all elements in an array except the current element
function f_gold(nums) {
    let n = nums.length;
    let ans = Array(n).fill(1);
    let left = 1, right = 1;
    // calculate the product of all elements to the left of the current element
    for (let i = 0; i < n; i++) {
        ans[i] = left;
        left *= nums[i];
    }
    // calculate the product of all elements to the right of the current element
    for (let i = n - 1; i >= 0; i--) {
        ans[i] *= right;
        right *= nums[i];
    }
    return ans;
}
