// define a function that finds the second largest digit in a string
function f_gold(s) {
    var largest_digit = -1, second_largest_digit = -1;
    // iterate through each character in the string
    for (var c of s) {
        // check if the character is a digit
        if (c.match(/^\d+$/)) {
            var num = parseInt(c);
            // if the digit is larger than the current largest digit, update both largest and second largest digits
            if (num > largest_digit) {
                second_largest_digit = largest_digit;
                largest_digit = num;
            }
            // if the digit is smaller than the largest digit but larger than the current second largest digit, update the second largest digit
            else if (num > second_largest_digit && num < largest_digit) {
                second_largest_digit = num;
            }
        }
    }
    // return the second largest digit
    return second_largest_digit;
}
