// function to find two missing numbers in an array of consecutive integers
const f_gold = (nums) => {
    let eor = 0; // initialize the variable eor to 0
    let n = nums.length; // get the length of the input array
    // loop through the array and calculate the XOR of all elements and the consecutive integers
    for (let i = 1; i < n + 1; i++) {
        eor ^= i ^ nums[i - 1];
    }
    // calculate the difference between eor and its 2's complement
    let diff = eor & (~eor + 1);
    let a = 0; // initialize variable a to 0
    // loop through the array and XOR the elements that have a 0 in the diff position
    // also XOR the consecutive integers that have a 0 in the diff position
    for (let i = 1; i < n + 1; i++) {
        if ((nums[i - 1] & diff) == 0) {
            a ^= nums[i - 1];
        }
        if ((i & diff) == 0) {
            a ^= i;
        }
    }
    let b = eor ^ a; // calculate the second missing number
    // loop through the array and check if a is equal to any of the elements
    // if it is, return a and b, otherwise return b and a
    for (let num of nums) {
        if (a == num) {
            return [a, b];
        }
    }
    return [b, a];
}
