function decode(encoded_message) {
    var message_length = encoded_message.length + 1;
    var xor_sum = 0;
    var xor_sum_without_missing_number = xor_sum;
    for (var i = 0; i < message_length - 1; i += 2) {
        xor_sum_without_missing_number ^= encoded_message[i];
    }
    for (var i = 0; i < message_length + 1; i++) {
        xor_sum ^= i;
    }
    var decoded_message = [xor_sum_without_missing_number ^ xor_sum];
    for (var encoded_number of encoded_message.reverse()) {
        decoded_message.push(decoded_message[decoded_message.length - 1] ^ encoded_number);
    }
    return decoded_message.reverse();
}
