function find_shortest_distance(wordsDict, word1, word2) {
    var index1 = -1, index2 = -1;
    var shortest_distance = wordsDict.length;
    var same_word = word1 == word2;
    for (var i = 0; i < wordsDict.length; i++) {
        if (same_word) {
            if (word1 == wordsDict[i]) {
                if (index1 != -1) {
                    shortest_distance = Math.min(shortest_distance, i - index1);
                }
                index1 = i;
            }
        } else {
            if (word1 == wordsDict[i]) {
                index1 = i;
            }
            if (word2 == wordsDict[i]) {
                index2 = i;
            }
            if (index1 != -1 && index2 != -1) {
                shortest_distance = Math.min(shortest_distance, Math.abs(index1 - index2));
            }
        }
    }
    return shortest_distance;
}
