"use strict";

function findMaxConsecutiveOnes(nums) {
    const n = nums.length;
    const prefix = new Array(n).fill(0);
    const suffix = new Array(n).fill(0);
    let res = 0;
    for (let i = 0; i < n; i++) {
        if (i === 0) {
            prefix[i] = nums[i];
        } else {
            prefix[i] = nums[i] === 0 ? 0 : prefix[i - 1] + 1;
        }
        res = Math.max(res, prefix[i]);
    }
    for (let i = n - 1; i >= 0; i--) {
        if (i === n - 1) {
            suffix[i] = nums[i];
        } else {
            suffix[i] = nums[i] === 0 ? 0 : suffix[i + 1] + 1;
        }
    }
    for (let i = 0; i < n; i++) {
        if (nums[i] === 0) {
            let t = 1;
            if (i > 0) {
                t += prefix[i - 1];
            }
            if (i < n - 1) {
                t += suffix[i + 1];
            }
            res = Math.max(res, t);
        }
    }
    return res;
}
