function f_gold(nums) {
    var m1 = -Infinity, m2 = -Infinity, m3 = -Infinity;
    for (var num of nums) {
        if ([m1, m2, m3].includes(num)) continue; // skip duplicates
        if (num > m1) { // update largest, shift others
            m3 = m2;
            m2 = m1;
            m1 = num;
        } else if (num > m2) { // update second largest, shift third largest
            m3 = m2;
            m2 = num;
