// function to find the minimum cost of a binary search tree
function f_gold(n) {
    // initialize a 2D array with 0s
    var dp = Array(n + 10).fill(0).map(() => Array(n + 10).fill(0));
    // loop through all possible lengths of subtrees
    for (var l = 2; l < n + 1; l++) {
        // loop through all possible starting indices of subtrees
        for (var i = 1; i < n - l + 2; i++) {
            // calculate the ending index of the current subtree
            var j = i + l - 1;
            // set the initial value of dp[i][j] to infinity
            dp[i][j] = Number.POSITIVE_INFINITY;
            // loop through all possible root nodes of the current subtree
            for (var k = i; k < j + 1; k++) {
                // calculate the cost of the current root node
                var t = Math.max(dp[i][k - 1], dp[k + 1][j]) + k;
                // update the minimum cost of the current subtree
                dp[i][j] = Math.min(dp[i][j], t);
            }
        }
    }
    // return the minimum cost of the entire binary search tree
    return dp[1][n];
}
