function find_subarrays_with_sum_greater_than_k(numbers, k) {
    let prefix_sum = [0].concat(numbers.map((sum => value => sum += value)(0)));
    let count = 0;
    for (let i = 1; i < numbers.length + 1; i++) {
        if (numbers[i - 1] >= k) continue;
        let left = 1;
        let right = i;
        while (left < right) {
            let mid = Math.floor((left + right + 1) / 2);
            if ((prefix_sum[i] - prefix_sum[i - mid]) * mid < k) {
                left = mid;
            } else {
                right = mid - 1;
            }
        }
        count += left;
    }
    return count;
}
