function find_minimum_days_to_eat_all_apples(apples, days_to_rot) {
    var queue = [];
    var num_of_apples = apples.length;
    var current_day = 0, minimum_days = 0;
    while (current_day < num_of_apples || queue.length > 0) {
        if (current_day < num_of_apples && apples[current_day] > 0) {
            queue.push([current_day + days_to_rot[current_day] - 1, apples[current_day]]);
        }
        while (queue.length > 0 && queue[0][0] < current_day) {
            queue.shift();
        }
        if (queue.length > 0) {
            var end = queue[0][0];
            var num = queue[0][1];
            num -= 1;
            minimum_days += 1;
            if (num > 0 && end > current_day) {
                queue.push([end, num]);
            }
        }
        current_day += 1;
    }
    return minimum_days;
}
