function f_gold(segments) {
    const d = new Map();
    for (const [l, r, c] of segments) {
        d.set(l, (d.get(l) || 0) + c);
        d.set(r, (d.get(r) || 0) - c);
    }
    const s = Array.from(d.entries()).sort((a, b) => a[0] - b[0]);
    const n = s.length;
    for (let i = 1; i < n; i++) {
        s[i][1] += s[i - 1][1];
    }
    return s.slice(0, n - 1).filter(([_, v]) => v).map(([l, _, c], i) => [l, s[i + 1][0], c]);
}
