function find_security_indices(security_prices, time_threshold) {
    var n = security_prices.length;
    if (n <= time_threshold * 2) {
        return [];
    }
    var left = Array(n).fill(0), right = Array(n).fill(0);
    for (var i = 1; i < n; i++) {
        if (security_prices[i] <= security_prices[i - 1]) {
            left[i] = left[i - 1] + 1;
        }
    }
    for (var i = n - 2; i >= 0; i--) {
        if (security_prices[i] <= security_prices[i + 1]) {
            right[i] = right[i + 1] + 1;
        }
    }
    var result = [];
    for (var i = 0; i < n; i++) {
        if (time_threshold <= Math.min(left[i], right[i])) {
