const { Deque } = require('double-ended-queue');

function f_gold(grid) {
    const n = grid.length;
    const q = new Deque();
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
            if (grid[i][j] === 1) {
                q.push([i, j]);
            }
        }
    }
    let ans = -1;
    let valid = false;
    while (q.length > 0) {
        ans += 1;
        const len = q.length;
        for (let i = 0; i < len; i++) {
            const [x, y] = q.shift();
            for (const [a, b] of [[0, 1], [0, -1], [1, 0], [-1, 0]]) {
                const i2 = x + a;
                const j2 = y + b;
                if (0 <= i2 && i2 < n && 0 <= j2 && j2 < n && grid[i2][j2] === 0) {
                    valid = true;
                    grid[i2][j2] = 1;
                    q.push([i2, j2]);
                }
            }
        }
    }
    return valid ? ans : -1;
}
