// function to find the size of the largest square submatrix with all 1's
function f_gold(matrix) {
    let m = matrix.length;
    let n = matrix[0].length;
    // initialize a 2D array with 0's
    let dp = new Array(m + 1);
    for (let i = 0; i < dp.length; i++) {
        dp[i] = new Array(n + 1).fill(0);
    }
    let mx = 0;
    // iterate through the matrix and update the dp array
    for (let i = 0; i < m; i++) {
        for (let j = 0; j < n; j++) {
            if (matrix[i][j] == '1') {
                dp[i + 1][j + 1] = Math.min(dp[i][j + 1], dp[i + 1][j], dp[i][j]) + 1;
                mx = Math.max(mx, dp[i + 1][j + 1]);
            }
        }
    }
    // return the area of the largest square submatrix
    return mx * mx;
}
