        result.push(processId);
        for (let childProcessId of processGraph[processId] ?? []) {
            depthFirstSearch(childProcessId);
        }
    }
    let processGraph = {};
    let numberOfProcesses = processIds.length;
    for (let i = 0; i < numberOfProcesses; i++) {
        if (processGraph[parentProcessIds[i]] === undefined) {
            processGraph[parentProcessIds[i]] = [];
        }
        processGraph[parentProcessIds[i]].push(processIds[i]);
    }
    let result = [];
    depthFirstSearch(killProcessId);
    return result;
}
