function find_gold_numbers(n) {
    function depth_first_search(u) {
        if (u == 0) {
            return [''];
        }
        if (u == 1) {
            return ['0', '1', '8'];
        }
        var ans = [];
        for (var v of depth_first_search(u - 2)) {
            for (var [left, right] of [['1', '1'], ['8', '8'], ['6', '9'], ['9', '6']]) {
                ans.push(left + v + right);
            }
            if (u != n) {
                ans.push('0' + v + '0');
            }
        }
        return ans;
    }
    return depth_first_search(n);
}
