function countSubstrings(s) {
    let t = `^#${s.split('').join('#')}#$`;
    let n = t.length;
    let p = new Array(n).fill(0);
    let pos = 0, maxRight = 0;
    let ans = 0;
    for (let i = 1; i < n - 1; i++) {
        p[i] = maxRight > i ? Math.min(maxRight - i, p[2 * pos - i]) : 1;
        while (t[i - p[i]] === t[i + p[i]]) {
            p[i]++;
        }
        if (i + p[i] > maxRight) {
            maxRight = i + p[i];
            pos = i;
        }
        ans += Math.floor(p[i] / 2);
    }
    return ans;
}
