// function to find the minimum length of a subarray whose sum is greater than or equal to k
function f_gold(nums, k) {
    // create an array s with cumulative sums of nums
    var s = [0].concat(nums.map((sum => value => sum += value)(0)));
    // initialize ans to positive infinity
    var ans = Number.POSITIVE_INFINITY;
    // create a queue q with the first element as 0
    var q = [0];
    // loop through s starting from index 1
    for (var i = 1; i < s.length; i++) {
        // remove elements from the front of q until s[i] - s[q[0]] is less than k
        while (q.length && s[i] - s[q[0]] >= k) {
            ans = Math.min(ans, i - q.shift());
        }
        // remove elements from the back of q until s[i] is greater than s[q[q.length - 1]]
        while (q.length && s[i] <= s[q[q.length - 1]]) {
            q.pop();
        }
        // add i to the back of q
        q.push(i);
    }
    // if ans is still positive infinity, return -1, otherwise return ans
    return (ans == Number.POSITIVE_INFINITY) ? -1 : ans;
}
