    function extendMap(map1, map2, queue) {
        for (var _ = 0, queueLength = queue.length; _ < queueLength; _++) {
            var currentWord = queue.shift();
            var currentStep = map1[currentWord];
            currentWord = currentWord.split('');
            for (var i = 0; i < currentWord.length; i++) {
                var currentChar = currentWord[i];
                for (var j = 0; j < 26; j++) {
                    currentWord[i] = String.fromCharCode(97 + j);
                    var transformedWord = currentWord.join('');
                    if (transformedWord in map1 || !(words.has(transformedWord))) continue;
                    if (transformedWord in map2) return currentStep + 1 + map2[transformedWord];
                    map1[transformedWord] = currentStep + 1;
                    queue.push(transformedWord);
                }
                currentWord[i] = currentChar;
            }
        }
        return -1;
    }
    var words = new Set(wordList);
    if (!(words.has(endingWord))) return 0;
    var queue1 = [beginningWord], queue2 = [endingWord];
    var map1 = {}, map2 = {};
    map1[beginningWord] = 0;
    map2[endingWord] = 0;
    while (queue1.length > 0 && queue2.length > 0) {
        var shortestPath = queue1.length <= queue2.length ? extendMap(map1, map2, queue1) : extendMap(map2, map1, queue2);
