### JavaScript

```javascript
const heapify = require('heapq').heapify;
const heappush = require('heapq').heappush;
const heappop = require('heapq').heappop;
const defaultdict = require('collections').defaultdict;

function f_gold(times, n, k) {
    const INF = 0x3F3F;
    const g = defaultdict(list);
    for (let i = 0; i < times.length; i++) {
        const [u, v, w] = times[i];
        g[u - 1].push([v - 1, w]);
    }
    const dist = Array(n).fill(INF);
    dist[k - 1] = 0;
    const q = [[0, k - 1]];
    while (q.length > 0) {
        const [_, u] = heappop(q);
        for (let i = 0; i < g[u].length; i++) {
            const [v, w] = g[u][i];
            if (dist[v] > dist[u] + w) {
                dist[v] = dist[u] + w;
                heappush(q, [dist[v], v]);
            }
        }
    }
    const ans = Math.max(...dist);
    return ans === INF ? -1 : ans;
}
