function countMatchingSubarrays(picture, target) {
  var numRows = picture.length;
  var numCols = picture[0].length;
  var rowCounts = Array(numRows).fill(0);
  var colIndices = new Map();
  for (var i = 0; i < numRows; i++) {
    for (var j = 0; j < numCols; j++) {
      if (picture[i][j] == 'B') {
        rowCounts[i] += 1;
        if (colIndices.has(j)) {
          colIndices.get(j).push(i);
        } else {
          colIndices.set(j, [i]);
        }
      }
    }
  }
  var isMatching = Array(numRows).fill(0).map(() => Array(numRows).fill(false));
  for (var i = 0; i < numRows; i++) {
    for (var k = i; k < numRows; k++) {
      if (i == k) {
        isMatching[i][k] = true;
      } else {
        isMatching[i][k] = true;
        for (var j = 0; j < numCols; j++) {
          if (picture[i][j] != picture[k][j]) {
            isMatching[i][k] = false;
            break;
          }
        }
      }
      isMatching[k][i] = isMatching[i][k];
    }
  }
  var numMatches = 0;
  for (var i = 0; i < numRows; i++) {
    if (rowCounts[i] == target) {
      for (var j = 0; j < numCols; j++) {
        if (colIndices.get(j) && colIndices.get(j).length == target && colIndices.get(j).every(k => isMatching[i][k])) {
          numMatches += 1;
        }
      }
    }
  }
  return numMatches;
}
