// export the module as an ES module
exports.__esModule = true;
// define a function to calculate a value based on input n
const f_gold = function (n) {
    // if n is 1, return 6
    if (n == 1)
        return 6;
    // define mod and dp arrays
    var mod = Math.pow(10, 9) + 7;
    var dp = Array(n + 1);
    // initialize dp array with 0s
    for (var i = 0; i < n + 1; i++) {
        dp[i] = Array(6);
        for (var j = 0; j < 6; j++) {
            dp[i][j] = Array(6).fill(0);
        }
    }
    // fill dp array with values for k=2
    for (var i = 0; i < 6; i++) {
        for (var j = 0; j < 6; j++) {
            if (gcd(i + 1, j + 1) == 1 && i != j)
                dp[2][i][j] = 1;
        }
    }
    // fill dp array with values for k>2
    for (var k = 3; k < n + 1; k++) {
        for (var i = 0; i < 6; i++) {
            for (var j = 0; j < 6; j++) {
                if (gcd(i + 1, j + 1) == 1 && i != j) {
                    for (var h = 0; h < 6; h++) {
                        if (gcd(h + 1, i + 1) == 1 && h != i && h != j)
                            dp[k][i][j] += dp[k - 1][h][i];
                    }
                }
            }
        }
    }
    // calculate the final answer
    var ans = 0;
    for (var i = 0; i < 6; i++) {
        for (var j = 0; j < 6; j++) {
            ans += dp[n][i][j];
        }
    }
    // return the answer modulo mod
    return ans % mod;
};
// define a function to calculate the greatest common divisor of two numbers
var gcd = function (a, b) {
    if (a == 0)
        return b;
    return gcd(b % a, a);
};
