function countWordsStartingWithSameLetter(inputString, wordsArray) {
    let wordBuckets = {};
    for (let word of wordsArray) {
        if (wordBuckets[word[0]] === undefined) {
            wordBuckets[word[0]] = [];
        }
        wordBuckets[word[0]].push(word);
    }
    let result = 0;
    for (let character of inputString) {
        let oldWords = wordBuckets[character] ? wordBuckets[character].slice() : [];
        wordBuckets[character] = [];
        for (let word of oldWords) {
            if (word.length == 1) {
                result += 1;
            }
            else {
                if (wordBuckets[word[1]] === undefined) {
                    wordBuckets[word[1]] = [];
                }
                wordBuckets[word[1]].push(word.slice(1));
            }
        }
    }
    return result;
}
