"use strict";

const f_gold = (arr) => {
    const idx = new Map();
    for (let i = 0; i < arr.length; i++) {
        if (!idx.has(arr[i])) {
            idx.set(arr[i], []);
        }
        idx.get(arr[i]).push(i);
    }
    const q = [[0, 0]];
    const vis = new Set([0]);
    while (q.length > 0) {
        const [i, step] = q.shift();
        if (i === arr.length - 1) {
            return step;
        }
        const v = arr[i];
        const newStep = step + 1;
        for (const j of idx.get(v)) {
            if (!vis.has(j)) {
                vis.add(j);
                q.push([j, newStep]);
            }
        }
        idx.delete(v);
        if (i + 1 < arr.length && !vis.has(i + 1)) {
            vis.add(i + 1);
            q.push([i + 1, newStep]);
        }
        if (i - 1 >= 0 && !vis.has(i - 1)) {
            vis.add(i - 1);
            q.push([i - 1, newStep]);
        }
    }
};
