```javascript
function is_num_palindrome(num) {
    function is_digit_match(digit1, digit2) {
        if (['0', '1', '8'].includes(digit1)) {
            return digit1 == digit2;
        }
        if (digit1 == '6') {
            return digit2 == '9';
        }
        if (digit1 == '9') {
            return digit2 == '6';
        }
        return false;
    }
    var num_length = num.length;
    var left_index = 0;
    var right_index = num_length - 1;
    while (left_index <= right_index) {
        if (!is_digit_match(num[left_index], num[right_index])) {
            return false;
        }
        left_index += 1;
        right_index -= 1;
    }
    return true;
}
