// define a function that converts a number to its English word representation
function f_gold(num) {
    // define arrays for numbers less than 20, multiples of ten, and thousands
    var lt20 = [
        '',
        'One',
        'Two',
        'Three',
        'Four',
        'Five',
        'Six',
