    ];
    var tens = [
        '',
        'Ten',
        'Twenty',
        'Thirty',
        'Forty',
        'Fifty',
        'Sixty',
        'Seventy',
        'Eighty',
        'Ninety',
    ];
    var thousands = ['Billion', 'Million', 'Thousand', ''];
    // define a recursive function to convert a number to its English word representation
    function transfer(num) {
        if (num == 0) {
            return '';
        }
        if (num < 20) {
            return lt20[num] + ' ';
        }
        if (num < 100) {
            return tens[Math.floor(num / 10)] + ' ' + transfer(num % 10);
        }
        return lt20[Math.floor(num / 100)] + ' Hundred ' + transfer(num % 100);
    }
    // initialize an empty array to store the English word representation of each section of the number
    var res = [];
    // initialize a variable to represent the current section of the number (billions, millions, thousands, or ones)
    var i = 1000000000;
    // initialize a variable to represent the current index in the thousands array
    var j = 0;
    // iterate through each section of the number, starting with billions and ending with ones
    while (i > 0) {
        // if the current section is not zero, add its English word representation to the result array
        if (Math.floor(num / i) != 0) {
            res.push(transfer(Math.floor(num / i)));
            res.push(thousands[j]);
            res.push(' ');
            num %= i;
        }
        // move on to the next section and index in the thousands array
        j += 1;
        i = Math.floor(i / 1000);
    }
    // join the English word representations of each section and return the result as a string
    return res.join('').trim();
}
