function find_longest_substring_with_equal_case(s) {
    var n = s.length;
    var longest_substring = '';
    for (var i = 0; i < n; i++) {
        var lower_case = 0;
        var upper_case = 0;
        for (var j = i; j < n; j++) {
            if (s[j].toLowerCase() === s[j]) {
                lower_case |= 1 << (s.charCodeAt(j) - 'a'.charCodeAt(0));
            }
            else {
                upper_case |= 1 << (s.charCodeAt(j) - 'A'.charCodeAt(0));
            }
            if (lower_case == upper_case && j - i + 1 > longest_substring.length) {
                longest_substring = s.substring(i, j + 1);
            }
        }
    }
    return longest_substring;
}
