            dfs(path, precursor)
            path.pop()
    ans = []
    words = set(wordList)
    if endWord not in words:
        return ans
    words.discard(beginWord)
    dist = {beginWord: 0}
    prev = defaultdict(set)
    q = deque([beginWord])
    found = False
    step = 0
    while q and not found:
        step += 1
        for i in range(len(q), 0, -1):
            p = q.popleft()
            s = list(p)
            for i in range(len(s)):
                ch = s[i]
