function findLadders(beginWord, endWord, wordList) {
    function depthFirstSearch(path, currentWord) {
        if (currentWord == beginWord) {
            answer.push(path.slice().reverse());
            return;
        }
        for (let precursor of previousWords[currentWord]) {
            path.push(precursor);
            depthFirstSearch(path, precursor);
            path.pop();
        }
    }
    let answer = [];
    let words = new Set(wordList);
    if (!words.has(endWord)) {
        return answer;
    }
    words.delete(beginWord);
    let distance = {};
    distance[beginWord] = 0;
    let previousWords = {};
    let queue = [beginWord];
    let found = false;
    let step = 0;
    while (queue.length > 0 && !found) {
        step += 1;
        for (let i = queue.length - 1; i >= 0; i--) {
            let current = queue.shift();
            let splitCurrent = current.split('');
            for (let i = 0; i < splitCurrent.length; i++) {
                let character = splitCurrent[i];
                for (let j = 0; j < 26; j++) {
                    splitCurrent[i] = String.fromCharCode(97 + j);
                    let transformedWord = splitCurrent.join('');
                    if (distance[transformedWord] == step) {
                        previousWords[transformedWord].add(current);
                    }
                    if (!words.has(transformedWord)) {
                        continue;
                    }
                    previousWords[transformedWord] = new Set();
                    previousWords[transformedWord].add(current);
                    words.delete(transformedWord);
                    queue.push(transformedWord);
                    distance[transformedWord] = step;
                    if (endWord == transformedWord) {
                        found = true;
                    }
                }
                splitCurrent[i] = character;
            }
        }
    }
    if (found) {
        let path = [endWord];
        depthFirstSearch(path, endWord);
    }
    return answer;
}
