// function to find the length of the longest subarray with equal number of 0's and 1's
function f_gold(nums) {
    let s = 0, ans = 0;
    let mp = {0: -1}; // initialize a dictionary with key 0 and value -1
    for (let i = 0; i < nums.length; i++) {
        s += nums[i] == 1 ? 1 : -1; // increment s if nums[i] is 1, otherwise decrement s
        if (s in mp) { // if s is already in the dictionary, update ans with the maximum of ans and i - mp[s]
            ans = Math.max(ans, i - mp[s]);
        }
        else { // otherwise, add s to the dictionary with value i
            mp[s] = i;
        }
    }
    return ans; // return the length of the longest subarray with equal number of 0's and 1's
}
```
