```javascript
function addNumbers(numArray, k) {
    var i = numArray.length - 1;
    var carry = 0;
    var resultArray = [];
    while (i >= 0 || k.length > 0 || carry) {
        carry += (i < 0 ? 0 : numArray[i]) + (k % 10);
        var v;
        [carry, v] = [Math.floor(carry / 10), carry % 10];
        resultArray.push(v);
        k = Math.floor(k / 10);
        i -= 1;
    }
    return resultArray.reverse();
}
