function find_maximum_distance(positions, minimum_distance) {
    function check(distance) {
        var previous_position = positions[0];
        var count = 1;
        for (var current_position of positions.slice(1)) {
            if (current_position - previous_position >= distance) {
                previous_position = current_position;
                count += 1;
            }
        }
        return count >= minimum_distance;
    }
    positions.sort((a, b) => (a - b));
    var left = 1;
    var right = positions[positions.length - 1];
    while (left < right) {
        var mid = (left + right + 1) >> 1;
        if (check(mid)) {
            left = mid;
        }
        else {
            right = mid - 1;
        }
    }
    return left;
}
