function filter_and_sort_numbers(nums) {
    var even_indexed_numbers = nums.filter((_, i) => i % 2 == 0).sort(function (a, b) { return a - b; });
    var odd_indexed_numbers = nums.filter((_, i) => i % 2 == 1).sort(function (a, b) { return b - a; });
    for (let i = 0; i < even_indexed_numbers.length; i++) nums[i * 2] = even_indexed_numbers[i];
    for (let i = 0; i < odd_indexed_numbers.length; i++) nums[1 + i * 2] = odd_indexed_numbers[i];
    return nums;
}
