// function to calculate the sum of degrees of all nodes in a graph
function f_gold(n, roads) {
    // create an array of size n and fill it with 0
    var deg = Array(n).fill(0);
    // loop through the roads array and increment the degree of each node
    for (var i = 0; i < roads.length; i++) {
        deg[roads[i][0]] += 1;
        deg[roads[i][1]] += 1;
    }
    // sort the degree array in ascending order
    deg.sort((a, b) => a - b);
    // calculate the sum of degrees multiplied by their index in the sorted array
    var sum = 0;
    for (var i = 0; i < deg.length; i++) {
        sum += (i + 1) * deg[i];
    }
    // return the sum
    return sum;
}
