// define a function that takes a string as input and returns a number
function f_gold(s) {
    // initialize four variables to zero
    var s0n0 = 0;
    var s0n1 = 0;
    var s1n0 = 0;
    var s1n1 = 0;
    // loop through each character in the input string
    for (var i = 0; i < s.length; i++) {
        // check if the index is even or odd
        if ((i & 1) == 0) {
            // if even, check if the character is '0' or not and update the corresponding variable
            if (s[i] != '0') {
                s0n0 += 1;
            }
            else {
                s1n1 += 1;
            }
        }
        else {
            // if odd, check if the character is '0' or not and update the corresponding variable
            if (s[i] != '0') {
                s1n0 += 1;
            }
            else {
                s0n1 += 1;
            }
        }
    }
    // check if the number of zeros and ones in each group are not equal, return -1 if true
    if (s0n0 != s0n1 && s1n0 != s1n1) {
        return -1;
    }
    // check if the number of zeros and ones in the first group are not equal, return the number of ones in the second group if true
    if (s0n0 != s0n1) {
        return s1n0;
    }
    // check if the number of zeros and ones in the second group are not equal, return the number of ones in the first group if true
    if (s1n0 != s1n1) {
        return s0n0;
    }
    // return the minimum number of zeros in both groups
    return Math.min(s0n0, s1n0);
}
