// define a function that takes a string and returns an array of all possible letter case permutations
function f_gold(s) {
    // define a recursive function that generates all possible permutations
    function dfs(i, t) {
        // if all characters have been processed, add the permutation to the answer array
        if (i == t.length) {
            ans.push(t.join(''));
            return;
        }
        // generate permutations without changing the current character's case
        dfs(i + 1, t);
        // if the current character is a letter, generate permutations with the current character's case changed
        if (t[i].match(/[a-z]/i)) {
            t[i] = t[i] === t[i].toLowerCase() ? t[i].toUpperCase() : t[i].toLowerCase();
            dfs(i + 1, t);
        }
    }
    // initialize an empty answer array and convert the input string to an array of characters
    var ans = [];
    var t = s.split('');
    // generate all possible permutations and return the answer array
    dfs(0, t);
    return ans;
}
