// function to find the longest substring with same case-insensitive characters
function f_gold(s) {
    var n = s.length;
    var ans = '';
    for (var i = 0; i < n; i++) {
        var lower = 0;
        var upper = 0;
        for (var j = i; j < n; j++) {
            if (s[j].toLowerCase() === s[j]) {
                lower |= 1 << (s.charCodeAt(j) - 'a'.charCodeAt(0));
            }
            else {
                upper |= 1 << (s.charCodeAt(j) - 'A'.charCodeAt(0));
            }
            if (lower == upper && j - i + 1 > ans.length) {
                ans = s.substring(i, j + 1);
            }
        }
    }
    return ans;
}
