from collections import Counter
from typing import *
def f_gold(values: List[int], labels: List[int], numWanted: int, useLimit: int
) -> int:
    arr = list(zip(values, labels))
    arr.sort(reverse=True)
    cnt = Counter()
    ans = num = 0
    for v, l in arr:
        if cnt[l] < useLimit:
            cnt[l] += 1
            num += 1
            ans += v
        if num == numWanted:
