// function to find the maximum length of a subarray with sum equal to k
function f_gold(nums, k) {
    // initialize a dictionary with key 0 and value -1
    var mp = { 0: -1 };
    var s = 0, ans = 0;
    // loop through the array and calculate the sum of elements
    for (var i = 0; i < nums.length; i++) {
        s += nums[i];
        // check if the difference between the current sum and k is in the dictionary
        if (s - k in mp) {
            // update the answer with the maximum of the current answer and the difference between the current index and the value in the dictionary
            ans = Math.max(ans, i - mp[s - k]);
        }
        // add the current sum to the dictionary if it is not already in there
        if (!(s in mp)) {
            mp[s] = i;
        }
    }
    // return the answer
    return ans;
}
