// define a function that takes an array of integers as input and returns an integer
function f_gold(stones) {
    // create an array of prefix sums from the input array
    var pre_sum = stones.map((sum => value => sum += value)(0));
    // initialize f to the last element of the prefix sum array
    var f = pre_sum[stones.length - 1];
    // iterate backwards through the prefix sum array, updating f as necessary
    for (var i = stones.length - 2; i > 0; i--) {
        f = Math.max(f, pre_sum[i] - f);
    }
    // return the final value of f
    return f;
}
