// function to calculate the maximum number of customers that can be satisfied
// considering the grumpy state of the seller during a given time period
function f_gold(customers, grumpy, minutes) {
    var s = 0; // sum of customers that could have been satisfied if the seller wasn't grumpy
    for (var i = 0; i < customers.length; i++) {
        s += customers[i] * grumpy[i];
    }
    var cs = 0; // total number of customers
    for (var i = 0; i < customers.length; i++) {
        cs += customers[i];
    }
    var t = 0; // sum of customers that could have been satisfied during the last 'minutes' minutes
    var ans = 0; // maximum number of customers that can be satisfied
    for (var i = 1; i <= customers.length; i++) {
        t += customers[i - 1] * grumpy[i - 1];
        if (i - minutes >= 0) {
            ans = Math.max(ans, cs - (s - t)); // update the maximum number of customers that can be satisfied
            t -= customers[i - minutes] * grumpy[i - minutes]; // remove the customers that were counted in the previous 'minutes' minutes
        }
    }
    return ans; // return the maximum number of customers that can be satisfied
}
