function find_minimum_steps_to_reach_end(arr) {
    let index_map = new Map();
    for (let i = 0; i < arr.length; i++) {
        if (index_map.has(arr[i])) {
            index_map.get(arr[i]).push(i);
        } else {
            index_map.set(arr[i], [i]);
        }
    }
    let queue = [[0, 0]];
    let visited = new Set();
    visited.add(0);
    while (queue.length > 0) {
        let current_index = queue[0][0];
        let current_step = queue[0][1];
        queue.shift();
        if (current_index == arr.length - 1) {
            return current_step;
        }
        let current_value = arr[current_index];
        current_step += 1;
        for (let j = 0; j < index_map.get(current_value)?.length; j++) {
            if (!visited.has(index_map.get(current_value)[j])) {
                visited.add(index_map.get(current_value)[j]);
                queue.push([index_map.get(current_value)[j], current_step]);
            }
        }
        index_map.delete(current_value);
        if (current_index + 1 < arr.length && !visited.has(current_index + 1)) {
            visited.add(current_index + 1);
            queue.push([current_index + 1, current_step]);
        }
        if (current_index - 1 >= 0 && !visited.has(current_index - 1)) {
            visited.add(current_index - 1);
            queue.push([current_index - 1, current_step]);
        }
    }
}
